#define MTIME_ADDR 0x200BFF8

size_t strlen(const char* str) 
{
    int count = 0; 
    while (*str != '\0') {
        str++; 
        count++; 
    }
    return count; 
}

uint32_t read_time(void) 
{
    volatile uint32_t *mtime = (uint32_t*)MTIME_ADDR;
    return *mtime;
}

